import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import Question from '../Question/Question';
import { number, string } from 'prop-types';

export default class Quiz extends Component {
  constructor(props){
    super();
    this.state = {
      rangevalue1: 3,
      rangevalue2: 3,
      rangevalue3: 3,
      rangevalue4: 3,
      rangevalue5: 3,

    }
    this.updateRange = this.updateRange.bind(this);
  }

  updateRange(event){
    const target = event.target;
    const value = target.type === 'range' ? target.name :target.value; //tengo que poner aqui lo que cambia el value
    const name = target.name;
    this.setState({
      [name]:value
    })
  }

  render() {
    return (
      <div>
        <h1>Quiz</h1>
        <h2>Hello person</h2>

        <div>
          <h3>You are a</h3> 
            <Question></Question>
            <h3>looking to find</h3> 
            <Question></Question>
        </div>
        <div className="quiz-father">
        <div>
        <h3>Conventional</h3><input name="Q1" type="range" min="1" max="5" value={rangevalue1} class="slider" id="myRange"></input><h3>Creative</h3>
        </div>
        <div>
        <h3>Calm</h3><input name="Q2" type="range" min="1" max="5" value={rangevalue2} class="slider" id="myRange"></input><h3>Active</h3>
        </div>
        <div>
        <h3>Organized</h3><input name="Q3" type="range" min="1" max="5" value={rangevalue3} class="slider" id="myRange"></input><h3>Spontaneous</h3>
        </div>
        <div>
        <h3>Reserved</h3><input name="Q4" type="range" min="1" max="5" value={rangevalue4} class="slider" id="myRange"></input><h3>Extroverted</h3>
        </div>
        <div>
        <h3>Disciplined</h3><input name="Q5" type="range" min="1" max="5" value={rangevalue5} class="slider" id="myRange"></input><h3>Relaxed</h3>
        </div>
        </div>
      </div>
    )}
}



ReactDOM.render(
  <Quiz />,
  document.getElementById('root')
);
